@bot.message_handler(content_types=['text'])

def get_text_messages(message):
    if message.text == "–ü—Ä–∏–≤–µ—Ç" or message.text == "–§–ª—é–≥–µ–≥–µ—Ö–∞–π–º–µ–Ω" or message.text == "–ë–ª—è" or message.text == "–ü–æ–ª—É–Ω–¥—Ä–∞":

        bot.send_message(message.from_user.id, "–°–∞–ª–∞–º, —â–∞ –≤—Å–µ –±—É–¥–µ—Ç")

        keyboard = types.InlineKeyboardMarkup()

        key_rus = types.InlineKeyboardButton(text='–ú–∞–Ω—Ç—Ä–∞', callback_data='infinite')

        keyboard.add(key_rus)

        key_mat = types.InlineKeyboardButton(text='–ú–∞—Ç–µ—Ä–∏–∞–ª—ã', callback_data='materials')

        keyboard.add(key_mat)
    
        bot.send_message(message.from_user.id, text='–®–æ –≤—ã–±–µ—Ä–µ—à—å, –¥–æ–±—Ä—ã–π –º–æ–ª–æ–¥–µ—Ü –∏–ª–∏ –ø—Ä–µ–∫—Ä–∞—Å–Ω–∞—è —Ü–∞—Ä–µ–≤–Ω–∞?', reply_markup=keyboard)
        
    elif message.text == "/help":

        bot.send_message(message.from_user.id, "–ù–∞–ø–∏—à–∏ –ø—Ä–∏–≤–µ—Ç –∏–ª–∏ —Å–ª–æ–≤–æ, –æ–ø–∏—Å—ã–≤–∞—é—â–µ–µ —Å–µ—Å—Å–∏—é")

    else:

        bot.send_message(message.from_user.id, "–ù–∏—á–µ–≥–æ –Ω–µ –ø–æ–Ω—è—Ç–Ω–æ. –ù–∞–ø–∏—à–∏ /help.")


@bot.callback_query_handler(func=lambda call: True)

def callback_worker(call):

    if call.data == "infinite": 
        bot.send_message(call.message.chat.id, infinite)
   
    elif call.data == "materials":
        kin = "https://drive.google.com/drive/u/0/folders/1STmW-IiL1dB9mHoGC9_AaaBLXGpGDFih"
        eng = "https://drive.google.com/drive/u/0/folders/1ZvWWl6v8wHjKE269B3qgyuqhHQt__OFP"
        bha = "https://drive.google.com/drive/u/0/folders/1dr0nZcEEVFExp0M5f15S01DQPo3U6lEf"
        gen = "https://drive.google.com/drive/u/0/folders/1BUgcLFIXJBtKFEGH0-oc1pUYPUGZhnKK"
        mic = "https://drive.google.com/drive/u/0/folders/19UVmcl3fKjdOtJMIZYe-aCS_N1x--ISu"
        mat = "–ö–∏–Ω–µ—Ç–∏–∫–∞:" + ' ' + kin + '\n' + "–ê–Ω–≥–ª–∏–π—Å–∫–∏–π:" + ' ' + eng + '\n' + "–ë–∏–æ—Ö–∏–º–∏—è:" + ' ' + bha + '\n' + "–ú–∏–∫—Ä–∞:" + ' ' + mic + '\n' + "–ì–µ–Ω–∏–Ω–∂:" + ' ' + gen + '\n'
        bot.send_message(call.message.chat.id, mat)

